import React, { useState, useEffect, useRef } from "react";
import Link from "./link";
export default () => {
  let arr = [1, 3, 4];
  const [POSI, setPOSI] = useState(null);
  const [open, setopen] = useState(false);
  const [he, sethe] = useState(0);
  const [childhe, setchlidhe] = useState(0);
  useEffect(() => {
    setTimeout(() => {
      let MainPosi = mainDom.getBoundingClientRect();
      initHeight.current = heightDom.offsetHeight;
      sethe(heightDom.offsetHeight); //获取最低高度
      setPOSI(MainPosi); // 获取位置信息
    });
  }, []);
  let mainDom;
  let heightDom;
  let initHeight = useRef(0);
  return (
    <div
      className="link-ctn"
      ref={(ref) => (mainDom = ref)}
      style={{ height: he }}
    >
      <div
        className="main pointer ani"
        ref={(ref) => (heightDom = ref)}
        onClick={() => {
          let h = initHeight.current;
          setopen((pre) => !pre);
          !open ? sethe(h + childhe * (arr.length + 1)) : sethe(h);
        }}
      >
        <div className="color"></div>
        <div className="title">
          <h2>CSS</h2>
        </div>
      </div>
      <div className="testbox"></div>
      {/* {arr.map((a, i) => (
        <Link
          mess={POSI}
          open={open}
          gao={childhe}
          key={i}
          setgao={setchlidhe}
        />
      ))} */}

      <style jsx>{`
        .link-ctn {
          width: 80%;
          margin: 20px auto;
          position: relative;
          transition: 0.5s;
          overflow: hidden;
        }
        .testbox {
          width: 100%;
          background: red;
        }
        .nice {
          position: absolute;
          left: 0;
          top: 0;
          width: 100%;
          z-index: 10;
        }
        .main {
          padding: 10px 20px;
          background: #212329;
          border-radius: 6px;
          display: flex;
          align-items: center;
        }

        .main > .color {
          width: 66px;
          height: 40px;
          border-radius: 4px;
          margin: 0 20px;
          background: #4eac6d;
        }
        .main > .title {
          flex: 1;
          font-size: 30px;
          text-align: center;
        }
      `}</style>
    </div>
  );
};
