import React, { useState, useEffect } from "react";

const Index = () => {
  const [oepn, setoepn] = useState(true);
  let arr = [1, 3, 4, 2];
  const oepnEvent = () => {
    setoepn((pre) => !pre);
  };
  return (
    <div className="box">
      <button onClick={oepnEvent}>open</button>
      {oepn && (
        <div className="ctn">
          {arr.map((a, i) => (
            <div className="box" key={i}></div>
          ))}
        </div>
      )}
      <style jsx>{`
        .ctn {
          width: 100%;
        }
        .box {
          width: 80%;
          height: 60px;
          background: blue;
          margin: 10px 0 10px 10%;
        }
      `}</style>
    </div>
  );
};

export default Index;
