import React, { useEffect, useRef } from "react";

function Link({ mess, open, gao, setgao }) {
  let once = useRef(false);
  useEffect(() => {
    let { current } = once;
    current ? (open ? zeroEv() : positionEv()) : (once.current = true);
  }, [open]);

  useEffect(() => {
    if (mess) positionEv();
    console.log(dom.offsetHeight);
    if (!gao) setgao(dom.offsetHeight);
  }, [mess]);
  const positionEv = () => {
    let posi = dom.getBoundingClientRect();
    dom.style.top = -(posi.top - mess.top) + "px";
  };
  const zeroEv = () => {
    dom.style.top = 0;
  };
  let dom;
  return (
    <div className="site ani pointer test " ref={(ref) => (dom = ref)}>
      <div className="backcolor">
        <h3>UI 参考网站 -- dribbbie</h3>
      </div>
      <style jsx>{`
        .site {
          width: 80%;
          margin: 20px 0 20px 20%;
          background: #212329;
          padding: 10px;
          border-radius: 4px;
          position: relative;
        }

        .site .backcolor {
          background: #4eac6d;
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
      `}</style>
    </div>
  );
}

export default Link;
