import React, { useEffect } from "react";

const Componetn = () => {
  const scrollEv = () => {
    //滚动动画事件
    let dom = componentRef.current;
    if (dom) {
      if (dom.getBoundingClientRect().top < window.innerHeight / 2) {
        let domList = dom.querySelectorAll(".cnt-item");
        Array.from(domList).map((e) => e.classList.add("cnt-item-show"));
      }
    }
  };
  let componentRef = React.createRef();
  useEffect(() => {
    window.addEventListener("scroll", scrollEv);
  }, []);
  return (
    <div className="row justify-content-center mx-0" ref={componentRef}>
      <div className="col-10 col-sm-6 m-5">
        <h2 className="text-center">查看一些我常用的组件(⺣◡⺣)♡</h2>
      </div>
      <div className="col-10 col-sm-8 cnt-item shadow-sm my-4">
        <a href="/component?type=React">
          <div className="col-12 d-flex justify-content-between p-4 pl-5">
            <h3> React Component </h3>
            <i className="iconfont icon-arrow-forward"></i>
          </div>
        </a>
      </div>
      <div className="col-10 col-sm-8 cnt-item shadow-sm my-4">
        <a href="/component?type=JS">
          <div className="col-12 d-flex justify-content-between p-4 pl-5">
            <h3> JS Component </h3>
            <i className="iconfont icon-arrow-forward"></i>
          </div>
        </a>
      </div>
      <div className="col-10 col-sm-8 cnt-item shadow-sm my-4">
        <a href="/component?type=CSS">
          <div className="col-12 d-flex justify-content-between p-4 pl-5">
            <h3> CSS Component </h3>
            <i className="iconfont icon-arrow-forward"></i>
          </div>
        </a>
      </div>
      <div className="col-10 col-sm-8 cnt-item shadow-sm my-4">
        <a href="/component?type=nodejs">
          <div className="col-12  d-flex justify-content-between p-4 pl-5 ">
            <h3> Node Component </h3>
            <i className="iconfont icon-arrow-forward"></i>
          </div>
        </a>
      </div>
      <div className="col-8 my-4">
        <h3 className="text-center more">
          查看更多组件？ 点击下面的按钮跳转(ง •_•)ง
        </h3>
      </div>
      <div className="col-10 col-sm-8 d-flex justify-content-center">
        <a href="/component">
          <button>点击 跳转 </button>
        </a>
      </div>
      <style jsx>
        {`
          .justify-content-center {
            background: #ebeef5;
            background: linear-gradient(45deg, #eff2f7, white);
            background: radial-gradient(#f4f7fc, white);
            padding: 6rem;
          }
          .cnt-item {
            background: white;
            border-radius: 6px;
            transition: 0.4s;
            cursor: pointer;
            transform: translateX(20%);
          }
          .cnt-item-show {
            transform: translateX(0);
          }
          .cnt-item:hover {
            transform: scale(1, 1.1);
          }
          .cnt-item:hover h3 {
            color: #309cfe;
            padding-left: 5rem;
          }
          .cnt-item:hover i {
            color: #309cfe;
            margin-right: 5rem;
          }
          h2 {
            font-size: 4rem;
            font-weight: 700;
          }
          h3 {
            font-size: 2rem;
            font-weight: 700;
            line-height: 2;
            transition: 0.4s;
          }
          h3.more {
            color: #a0a7af;
          }
          a,
          a:hover {
            color: black;
            text-decoration: none;
          }
          i {
            font-size: 3rem;
            margin-right: 3rem;
            transition: 0.4s;
          }
          button {
            background: black;
            border: none;
            border-radius: 6px;
            color: white;
            padding: 1rem 2rem;
            font-size: 2rem;
            font-weight: 700;
            transition: 0.2s;
          }
          button:hover {
            transform: scale(1.1);
            box-shadow: 0 0 4px black;
          }
          @media (max-width: 960px) {
            .cnt-item {
              transform: translateX(5%);
            }
          }
        `}
      </style>
    </div>
  );
};

export default Componetn;
