import React, { useEffect, useState } from "react";

const Footer = () => {
  const [status, setstatus] = useState(false);
  const lookHeightFunc = () => {
    console.log("object");
    let winH = window.innerHeight;
    let domH = document.documentElement.offsetHeight;
    if (winH > domH) setstatus(true);
  };
  useEffect(() => {
    lookHeightFunc();
    window.addEventListener("resize", lookHeightFunc);
  }, []);
  return (
    <footer className={`row mx-0 p-0 ${status ? "bottom" : ""}`}>
      <div className="col-12 py-5 mx-0 text-center">
        <h3>2020 Have a good night</h3>
      </div>
      <style jsx>
        {`
          footer {
            background: #326fa0;
            color: white;
            margin-top: 10%;
            width: 100%;
          }
          .bottom {
            position: fixed;
            bottom: 0;
            left: 0;
            z-index: 100;
          }
          h3 {
            font-size: 2rem;
          }
        `}
      </style>
    </footer>
  );
};

export default Footer;
