import React, { useEffect, useRef } from "react";

function Link({ mess, open, gao, setgao, mes, cor }) {
  let once = useRef(false);
  let initH = useRef(0);
  useEffect(() => {
    let { current } = once;
    current ? (open ? zeroEv() : positionEv()) : (once.current = true);
  }, [open]);

  useEffect(() => {
    if (mess) positionEv();
  }, [mess]);
  const positionEv = () => {
    let posi = dom.getBoundingClientRect();
    if (!gao) setgao(dom.offsetHeight);
    !initH.current ? (initH.current = -(posi.top - mess.top + 6)) : null;
    dom.style.top = initH.current + "px";
  };
  const zeroEv = () => {
    dom.style.top = 0;
  };
  let dom;

  return (
    <div className="site ani pointer test " ref={(ref) => (dom = ref)}>
      <div className="min-ctn ">
        <div className="backcolor" style={{ backgroundColor: cor }}>
          <a href={mes.url} target="_black">
            <h3>{mes.name}</h3>
          </a>
        </div>
      </div>
      <style jsx>{`
        .site {
          width: 80%;
          margin-left: 20%;
          position: relative;
          padding: 20px 0;
        }
        .min-ctn {
          padding: 10px;
          border-radius: 4px;
          background: #212329;
        }
        a {
          text-decoration: none;
        }
        .site .backcolor {
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
      `}</style>
    </div>
  );
}

export default Link;
