          {arr.map((a, i) => (
            <div className="box" key={i} style={{ top: oepn ? -20 : 0 }}></div>
          ))}
        </div>
      )}
      <style jsx>{`
        .ctn {
          width: 100%;
        }
        .box {
          width: 80%;
          height: 60px;
          background: blue;
          margin: 10px 0 10px 10%;
          position: relative;
          transition: 0.5s;
        }
      `}</style>
    </div>
  );
};

export default Index;
