import React from "react";

const About = ({ message, linkList }) => {
  return (
    <div className="row about m-0 p-0 justify-content-center">
      <div className="col-10 col-ls-6 ">
        <div className="row">
          <div className="col-12">
            <h3 className="my-4">关于文章</h3>
          </div>
          <div className="col-12">
            <h4>{message}</h4>
          </div>
          <div className="col-12">
            <div className="row">
              {linkList.map((link, index) => (
                <h5 className="my-4 col-auto" key={index}>
                  <a href={link.link} target="_blank">
                    {link.linkTitle}
                  </a>
                </h5>
              ))}
            </div>
          </div>
        </div>
      </div>
      <style>{`
                .about {
                    border-bottom:2px solid #eaeaea;
                    padding:10rem 0;
                }
                h3 {
                   font-size:2.5rem;
                   line-height:2;
               }
               h4 {
                   font-size:2rem;
                   line-height:1.5;
               }
               h5 {
                   cursor:pointer;
                   font-size:1.8rem;
                   color:#7bbcfc;
                   line-height:1.5;
               }
            `}</style>
    </div>
  );
};

export default About;
