import React, { useState } from "react";
import Link from "./link";
export default () => {
  const [POSI, setPOSI] = useState({});
  useEffect(() => {
    setTimeout(() => {
      console.log(mainDom);
      let MainPosi = mainDom.getBoundingClientRect();
      setPOSI(MainPosi);
    });
  }, []);
  let mainDom;
  return (
    <div className="link-ctn" ref={(ref) => (mainDom = ref)}>
      <div className="main pointer ani" onClick={() => setopen((pre) => !pre)}>
        <div className="color"></div>
        <div className="title">
          <h2>CSS</h2>
        </div>
      </div>
      <Link />
      <style jsx>{`
        .link-ctn {
          width: 80%;
          margin: 20px auto;
          position: relative;
        }
        .main {
          padding: 10px 20px;
          background: #212329;
          border-radius: 6px;
          display: flex;
          align-items: center;
          z-index: 10;
        }

        .main > .color {
          width: 66px;
          height: 40px;
          border-radius: 4px;
          margin: 0 20px;
          background: #4eac6d;
        }
        .main > .title {
          flex: 1;
          font-size: 30px;
          text-align: center;
        }
      `}</style>
    </div>
  );
};
