import React from "react";
import Photo from "../public/getPhoto";

const Author = ({
  ArticleAuthor,
  ArticleTitle,
  ArticleAuthorPhoto,
  createTime,
  comment,
}) => {
  let time = String(new Date(Number(createTime))).slice(0, 15);

  return (
    <div className="row author mx-0">
      <div className="col-10 offset-1">
        <div className="row">
          <div className="col-12 col-ls-6">
            <h3 className="my-5">作者</h3>
            <div className="author-message d-flex align-items-around">
              <div className="author-photo-cts">
                <Photo
                  id={"addArticle/gotArticlePhoto/" + ArticleAuthorPhoto}
                />
              </div>

              <div className="message ml-4 d-flex flex-column justify-content-around">
                <h2>{ArticleAuthor}</h2>
                <h3 className="">{ArticleTitle}</h3>
              </div>
            </div>
          </div>
          <div className="col-6 col-ls-3 ">
            <h3 className="my-0 mt-5 my-ls-5 mt-ls-0">发布于</h3>
            <h2 className="">
              <i></i>
              {time}
            </h2>
          </div>
          <div className="col-6 col-ls-3">
            <h3 className="my-0 mt-5 my-ls-5 mt-ls-0">评论</h3>
            <h2>
              <i></i>
              {comment.length} 评论
            </h2>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .author {
            margin: 10rem 0;
            padding-bottom: 8rem;
            border-bottom: 2px solid #eaeaea;
          }
          .author-photo-cts {
            height: 80px;
            width: 80px;
          }
          @media (max-width: 860px) {
            .message h3 {
              line-height: 1.1;
            }
            .author {
              margin-top: 4rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Author;
