import React, { useEffect, useState, useRef } from "react";
import Head from "next/head";
import Nav from "../component/photo/nav";
import MinNav from "../component/photo/min-nav";
import Item from "../component/photo/item";
import fetch from "node-fetch";
import Router from "next/router";

const Photo = ({ data }) => {
  const [showData, setshowData] = useState(data);
  const AllData = useRef();
  AllData.current = data;
  const filterEvent = (value) => {
    if (value === 0) return setshowData(AllData.current);

    let newData = AllData.current.filter((e) => {
      let PhotoTime = +new Date(Number(e.createTime));
      let liveTime = Date.now();
      if (liveTime - PhotoTime >= 1000 * 60 * 60 * 24 * value) {
        return e;
      }
    });
    setshowData(newData);
  };
  const [routerStatus, setRouterStatus] = useState(false); // 路由变化事件
  const handleRouteChange = (url) => {
    setRouterStatus(true);
  };
  Router.events.on("routeChangeStart", handleRouteChange);

  const fontsizeEvent = () => {
    let style = document.documentElement.style;
    if (window.innerWidth < 960) {
      style.fontSize = "6px";
    } else {
      style.fontSize = "10px";
    }
  };
  useEffect(() => {
    fontsizeEvent();
    window.addEventListener("resize", () => {
      fontsizeEvent();
    });
  }, []);

  return (
    <>
      <Head>
        <title>Create Next App</title>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_jonwjrvk6jq.css"
        />
        <title> 图片收藏 </title>
      </Head>
      <div className="container-fluid">
        <nav>
          <Nav />
        </nav>
        {showData.map((e) => (
          <Item key={e._id} {...e} />
        ))}
        {showData.length === 0 && (
          <h1 className="text-center mt-5"> 没有相关内容 </h1>
        )}
        <MinNav filter={filterEvent} />

        {routerStatus && (
          <div className="loadAnima"> {/*  路由变化时 提示动画 */}</div>
        )}
      </div>

      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
        .container-fluid {
          padding: 0;
        }
      `}</style>
      <style jsx>
        {`
          .loadAnima {
            position: fixed;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            background: black;
            animation: Loading 2s 10;
            z-index: 9999;
          }
          @keyframes Loading {
            0% {
              opacity: 0;
            }
            50% {
              opacity: 1;
            }
            100% {
              opacity: 0;
            }
          }
        `}
      </style>
    </>
  );
};
export async function getServerSideProps() {
  const RURL = process.env.DATA_URL + "addphoto/photos";
  let req = await fetch(RURL);
  let data = await req.json();
  return {
    props: { data },
  };
}
export default Photo;
