import React, { useRef } from "react";
import styles from "./minNav.module.css";
const MinNav = ({ setState, setLoad, setType, list, oldState, setPage }) => {
  let dom = useRef();
  const updateShow = async (e) => {
    try {
      let activeDom = e.target.closest("li");
      if (!activeDom) return;
      let oldDom = dom.current;
      if (oldDom) oldDom.classList.remove("seled");
      let type = activeDom.children[0].textContent;
      if (type === "All") {
        setState(oldState.data);
        setPage(oldState.count);
        setType(null);
        return;
      }
      activeDom.classList.add("seled"); //选中的元素添加样式
      dom.current = activeDom;

      setState([]); //搜索指定文章
      setLoad(true);
      let data = await gotData(type);
      if (Array.isArray(data.data)) {
        setState(data.data);
        setPage(data.count);
        setType(type);
      }
      setLoad(false);
    } catch (err) {
      if (err) console.log(err);
      console.log("列表元素样式切换失败");
    }
  };
  const searchEvent = async (e) => {
    e.stopPropagation();
    let actionDom = e.target.closest("li");
    let searchDom = document.querySelector("i.active");
    if (searchDom) {
      let dom = document.querySelector('input[name="search"]');
      let value = dom.value[0].toUpperCase() + dom.value.slice(1);
      setLoad(true);
      let data = await gotData(value);
      if (Array.isArray(data.data)) {
        setState(data.data);
        setPage(data.count);
        setType(value);
        dom.value = "";
        searchDom.classList.remove("active");
        actionDom.click();
      }
      setLoad(false);
      return;
    }

    let dom = document.querySelector(".search-input.focus");
    if (dom) {
      dom.classList.remove("focus");
      actionDom.classList.remove("seled");
    } else {
      dom = document.querySelector(".search-input");
      dom.classList.add("focus");
    }
  };
  const gotData = async (type) => {
    try {
      const RURL =
        process.env.DATA_URL + "addArticle/gotArticle/type/" + type + "?page=1";
      let res = await fetch(RURL);
      let result = await res.json();
      return result;
    } catch (err) {
      console.log(err);
      return {
        err,
      };
    }
  };

  return (
    <div className="row min-nav-ctn m-0">
      <div className="col-12 .col-md-11 min-nav  p-5 pffset-0 ">
        <ul className="d-flex m-0" onClick={updateShow}>
          {list.map((type, index) => (
            <li key={index}>
              <h3>{type}</h3>
            </li>
          ))}
          <li>
            <h3>All</h3>
          </li>

          <li onClick={searchEvent}>
            <h3>
              <i className="iconfont icon-search"></i>
            </h3>
          </li>
        </ul>
      </div>
      <div className={styles.searchCtn}>
        <input type="text" />
        <button type="button">搜索</button>
        <button type="button" className="close-search">
          <svg
            t="1591706026501"
            class="icon"
            viewBox="0 0 1024 1024"
            version="1.1"
            xmlns="http://www.w3.org/2000/svg"
            p-id="3269"
            width="20"
            height="20"
          >
            <path
              d="M571.7 514.1l164.9-164.9c16.3-16.3 16.3-43.1 0-59.4-16.3-16.3-43.1-16.3-59.4 0L512.3 454.7 347.4 289.8c-16.3-16.3-43.1-16.3-59.4 0-16.3 16.3-16.3 43.1 0 59.4l164.9 164.9L288 679c-16.3 16.3-16.3 43.1 0 59.4 16.3 16.3 43.1 16.3 59.4 0l164.9-164.9 164.9 164.9c16.3 16.3 43.1 16.3 59.4 0 16.3-16.3 16.3-43.1 0-59.4L571.7 514.1z"
              p-id="3270"
            ></path>
          </svg>
        </button>
      </div>
      <style jsx>
        {`
          .min-nav-ctn {
            transform: translateY(-150%);
          }
          .min-nav {
            color: white;
            background: black;
            position: relative;
            z-index: 10;
          }
          .close-search {
            background-color: white;
          }
          .close-search:hover {
            background-color: black;
          }
          ul {
            list-style: none;
          }
          li {
            flex: 1;
            cursor: pointer;
            transition: 0.4s;
            text-align: center;
          }
          li:hover,
          li.seled {
            opacity: 0.5;
            transform: translateY(-25%);
          }
          h3 {
            font-size: 2.5rem;
            text-align: center;
          }
          i {
            font-size: 2.5rem;
          }
          i.active {
            color: #133d93;
          }
          @media (max-width: 980px) {
            .min-nav {
              transform: translateY(00%);
            }
          }
          @media (max-width: 1100px) {
            .search-input input {
              font-size: 1.2rem;
            }
          }
          @media (max-width: 800px) {
            h3 {
              font-size: 2rem;
            }
          }
          @media (max-width: 400px) {
            h3 {
              font-size: 1rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default MinNav;
