import React , { useEffect } from 'react'
import LeftRoot from './left-root'
import RightForm from './textarea'


const Root = ({rest}) => {
    
    return (
        <div className='add-component-new-root row align-items-center'>
               <div className="col-4">
                     <LeftRoot/>
               </div>
               <div className="col-8">
                    <RightForm restEvent={rest}/>
               </div>
                <style jsx> {`
                        .add-component-new-root {
                                position:fixed;  
                                width:100%;          
                                top:0;
                                right:0;
                                left:0;
                                bottom:0;                                
                                animation: show 1s 1 ;                                
                                background-image:url('greed.jpg');
                                background-size:cover;

                            }
                            @keyframes show {
                                    0% {
                                        transform:scale(1.2);
                                    }
                                    100 {
                                    transform:scale(1);
                                    }
                                }                    
                                
                                `}
                </style>
        </div>
    )
}

export default Root
