import React from 'react'

const Photo = ({file,clear}) => {
    return (
       <div className="col-auto h-100 p-0 position-relative">
           <div className="photo-root">
               <div className="photo-bg">
                   {file && <img src={file} alt="newPhoto"/> }                    
               </div>
           </div>
            
            <div className="gongneng-root justify-content-around">
                <div className="delete-photo">
                    <i className='iconfont icon-trash-' onClick={() => clear()}>                  
                    </i>
                </div> 
                <div className="upload-photo">
                    <button type="submit">
                        <i className='iconfont icon-cloud-upload'>                  
                        </i>
                    </button>
                </div>             
            </div>
            <style jsx> {`
             
             
             img {
                  width:100%;
             }
             button {
                 background:none;
                 border:none;
             }
             .gongneng-root {
                 width:100%;
                 height:100px;              
                 position:absolute;
                 bottom:-100px;
                 display:flex;
                 align-items:center;                
             }
             .delete-photo,.upload-photo {                
                 height:60px;
                 width:60px;                 
                 border-radius:6px;
                 display:flex;
                 align-items:center;
                 justify-content:center;                
                 transition:.4s;
                 cursor:pointer;
             }
             .delete-photo:hover,.upload-photo:hover {
                border:solid 2px white;
             }
             .delete-photo:hover>i,.upload-photo:hover i {
                font-size:40px;
             }
             i {
                 color:white;
                 font-size:20px;
                 transition:.2s;
             }
             .photo-root {
                 width:600px;
                 height:600px;
                 background:white;
                 padding:50px;
             }
             .photo-bg {
                 width:500px;
                 height:500px;
                 background:black;
                 display:flex;
                 justify-content:center;
                 align-items:center; 
                 overflow:auto;           
             }
             .photo-bg::-webkit-scrollbar {
                 display:none;
             }
                        `}
            </style>                               
       </div>
    )
}

export default Photo
