import React, { useEffect, useState } from "react";
import Head from "next/head";
import Nav from "../component/public/nav";
import Title from "../component/home/title";
import fetch from "node-fetch";
import Introd from "../component/home/introd";
import Skill from "../component/home/skill";
import PostPhoto from "../component/home/PostPhoto";
import Component from "../component/home/component";
import Video from "../component/home/video";
import Footer from "../component/home/footer";
import Router from "next/router";

const Home = ({ data }) => {
  const fontsizeEvent = () => {
    let style = document.documentElement.style;
    if (window.innerWidth < 960) {
      style.fontSize = "6px";
    } else {
      style.fontSize = "10px";
    }
  };
  useEffect(() => {
    fontsizeEvent();
    window.addEventListener("resize", () => {
      fontsizeEvent();
    });
  }, []);

  const [routerStatus, setRouterStatus] = useState(false); // 路由变化事件
  const handleRouteChange = (url) => {
    setRouterStatus(true);
  };
  Router.events.on("routeChangeStart", handleRouteChange);

  return (
    <>
      <Head>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_jonwjrvk6jq.css"
        />
        <link rel="icon" href="/logo.jpg" type="image/x-icon"></link>
        <title> ZF </title>
      </Head>
      <div className="container-fluid px-0 mx-0">
        <Nav title={<Title />}>
          <img src="nav-bg.jpg" alt="bg" />
        </Nav>
        <Introd />

        <Skill />

        <PostPhoto data={data} />

        <Video />

        <Component />

        <Footer />
        {routerStatus && (
          <div className="loadAnima"> {/*  路由变化时 提示动画 */}</div>
        )}
      </div>
      <style jsx>
        {`
          .loadAnima {
            position: fixed;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            background: black;
            animation: Loading 2s 10;
            z-index: 9999;
          }
          @keyframes Loading {
            0% {
              opacity: 0;
            }
            50% {
              opacity: 1;
            }
            100% {
              opacity: 0;
            }
          }
        `}
      </style>
      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
      `}</style>
    </>
  );
};
export async function getServerSideProps() {
  const RURL = process.env.DATA_URL + "addphoto/photoFine";
  let req = await fetch(RURL);
  let data = await req.json();
  return {
    props: { data },
  };
}
export default Home;
