import React from "react";

const Title = () => {
  return (
    <div className="title row mx-0">
      <div className="col-10 offset-1">
        <div className="row title-container">
          <div className="col-6">
            <div className="row d-flex flex-column pl-4 justify-content-center">
              <h1>Instantly</h1>
              <h2>most important</h2>
              <p className="w-60">
                Cherish every day and try to make yourself happy !!
              </p>
            </div>
          </div>
          <div className="col-6 ">
            <div className="row justify-content-center">
              <div className="col-10 ">
                <img src="me.jpg" alt="logo" />
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .title {
            height: 86%;
            width: 100%;
            color: white;
          }
          img {
            width: 100%;
            position: relative;
            top: 20%;
            animation: photo 1s 1;
          }
          .title-container,
          .title-container .row {
            height: 100%;
          }
          h1 {
            font-size: 8rem;
            animation: text 0.6s 1 0s;
          }
          h2 {
            font-size: 4rem;
            animation: text 0.6s 1 0.2s;
          }
          p {
            font-size: 2rem;
            font-weight: 600;
            animation: text 0.6s 1 0.4s;
          }
          @keyframes photo {
            0% {
              transform: scale(0, 0);
            }
            100% {
              transform: scale(1, 1);
            }
          }

          @keyframes text {
            0% {
              transform: translateY(100%);
              opacity: 0;
            }
            100% {
              transform: translateY(0);
              opacity: 1;
            }
          }
          @media (max-width: 500px) {
            h1 {
              font-size: 4rem;
            }
            h2 {
              font-size: 2rem;
            }
          }
          @media (max-width: 380px) {
            h1 {
              font-size: 2rem;
            }
            h2 {
              font-size: 1rem;
            }
            p {
              line-height: 1.8rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Title;
