import React, { useEffect, useState, useRef } from "react";
import Head from "next/head";
import SiteItem from "../component/site/index";
import axios from "axios";
const Home = () => {
  const [data, setdata] = useState(null);
  const [quit, setquit] = useState(false);
  const [error, seterror] = useState(false);
  useEffect(() => {
    let reqFunc = async () => {
      try {
        let res = await axios("http://localhost:4040/site/gotData");

        if (!quit) setdata(res.data);
      } catch (err) {
        console.log(err);
        seterror(true);
      }
    };
    reqFunc();
  }, []);
  return (
    <>
      <Head>
        <title>网站收藏</title>
      </Head>
      <div className="container">
        <div className="content guodu">
          {data && data.map((s) => <SiteItem data={s} key={s._id} />)}
        </div>
        {error && <h1> 网页出错了 刷新试试</h1>}
      </div>
      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
          color: white;
        }
        .container {
          position: fixed;
          left: 0;
          top: 0;
          bottom: 0;
          right: 0;
          z-index: 2;
          background: #191b1f;
        }
        .content {
          position: absolute;
          left: 50%;
          top: 50%;
          width: 1080px;
          min-height: 600px;
          transform: translate(-50%, -50%);
        }
        h1 {
          text-align: center;
          margin: 20px;
        }
        .pointer {
          cursor: pointer;
        }
        .flex {
          display: flex;
          justify-content: center;
          align-items: center;
        }
        ul {
          list-style: none;
        }
        .ani,
        .guodu {
          transition: 0.5s;
        }
        .ani:hover {
          transform: scale(1.05);
        }
      `}</style>
    </>
  );
};

export default Home;
