import React, { useEffect, useState, useCallback } from "react";
import Code from "./code";
import Pagination from "./Pagination";
import { connect } from "react-redux";
import Anima from "../public/anima";
const Content = ({ type, action, data, loading }) => {
  const [code, setCode] = useState(null);
  const [shareS, setShareS] = useState(false);
  const getphoto = async () => {
    try {
      action({
        type: "LADING_CONTENN",
        loading: true,
      });
      action({
        type: "ALL_CONTENT",
        data: [],
      });
      const QURL = process.env.DATA_URL + "getComponent/get/" + type;
      let get = await fetch(QURL);
      if (get.ok) {
        let message = await get.json();
        console.log(message);
        action({
          type: "ALL_CONTENT",
          data: message,
        });
        action({
          type: "LADING_CONTENN",
          loading: false,
        });
      }
    } catch (err) {
      console.log(err);
    }
  };
  const arrayResult = useCallback(
    data.filter((e) => !e.filter),
    [data]
  );
  useEffect(() => {
    getphoto();
  }, [type]);
  const shareEvent = (boob) => {
    setCode(false);
    if (boob) {
      setShareS(true);
      setTimeout(() => setShareS(false), 1500);
    }
  };
  return (
    <div className="row code-content">
      {data.length > 0 && (
        <>
          <div className="col-12 code-count">
            <h1>显示 {arrayResult.length} 个组件</h1>
          </div>
          <Pagination type={type} codeList={arrayResult} setCode={setCode} />
        </>
      )}

      {data.length === 0 && !loading && <h1> 0 组件 </h1>}

      {loading && (
        <div className="col-12 my-5 d-flex justify-content-center">
          <Anima color="#fdd303" />
        </div>
      )}
      {code && <Code data={code} clear={shareEvent} />}
      {shareS && (
        <div className="sussWindow d-flex align-items-center justify-content-center">
          <h2>复制成功。</h2>
        </div>
      )}

      <style jsx>
        {`
          h3 {
            margin: 2rem 1rem;
            font-size: 2rem;
          }
          .code-count {
            margin-bottom: 10rem;
          }
          .sussWindow {
            position: fixed;
            left: 50%;
            top: 50%;
            width: 160px;
            height: 160px;
            background: rgba(0, 0, 0, 0.8);
            color: white;
            border-radius: 10px;
            transform: translate(-50%, -50%);
            font-weight: 600;
          }
        `}
      </style>
    </div>
  );
};
const stateEvent = (state) => ({
  data: state.ContentReducer,
  loading: state.TypeReducer.loading,
});
const dispatch = (dispatch) => ({ action: dispatch });

export default connect(stateEvent, dispatch)(Content);
