import React, { useEffect, useState } from "react";
import WindowMessage from "./messageWindow";
const Introd = () => {
  const scrollEv = () => {
    let value = window.pageYOffset;
    if (value > 600) {
      window.removeEventListener("scroll", scrollEv);
      if (!textDom) return;
      textDom.style.transform = `translate(0 ,0)`;
    } else {
      let count = 600 - value;
      count = count < 0 ? 0 : count;
      if (!textDom) return;
      textDom.style.transform = `translate(0 ,${count}px)`;
    }
  };
  useEffect(() => {
    if (window.innerWidth > 1200) {
      window.addEventListener("scroll", scrollEv);
    } else {
      textDom.style.transform = `translate(0 ,0)`;
    }
  }, []);
  let textDom;
  const [status, setStatus] = useState(false);
  return (
    <div className="row introd justify-content-center mx-0">
      {status && <WindowMessage set={setStatus} />}

      <div className="col-10 col-xl-8  position-relative">
        <div className="row">
          <div
            className="col-12 col-lg-8 text-center text-lg-left text-root"
            ref={(ref) => (textDom = ref)}
          >
            <h2>
              我叫郑奋，来自美丽的广东省揭阳市。 <br />
              熟悉开发网站和图像处理。 视频处理等等 <br />
            </h2>
            <p className="mt-5">如果需要我的帮助，点击右方按钮告诉我。</p>
          </div>
          <div className="col-12 col-lg-4">
            <div className="row h-100  align-items-end justify-content-lg-end justify-content-center">
              <button
                className="introd-bu   p-4 shadow-sm"
                onClick={() => setStatus(true)}
              >
                发送信息
              </button>
            </div>
          </div>
        </div>
      </div>

      <style jsx>
        {`
          .introd {
            margin-top: 16rem;
          }
          .text-root {
            transform: translateY(400px);
          }
          h2 {
            width: 100%;
            font-size: 4rem;
          }
          p {
            font-size: 2rem;
            font-weight: 600;
            text-decoration: underline;
          }
          .introd-bu {
            border: none;
            background: black;
            color: white;
            border-radius: 6px;
            font-size: 2rem;
            font-weight: 700;
            transition: 0.2s;
          }
          .introd-bu:hover {
            transform: scale(1.1);
            box-shadow: 0 0 4px black;
          }
          @media (max-width: 960px) {
            .introd-bu {
              margin: 5rem 0;
            }
          }
          @media (max-width: 660px) {
            h2 {
              font-size: 3rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Introd;
