import React, { useEffect, useRef } from "react";

const MinNav = ({ filter }) => {
  const clickEvent = (e) => {
    let dom = e.target.closest("h2");
    if (!dom) return;
    bingoRef.current.classList.remove("bingo");
    dom.parentElement.classList.add("bingo");
    bingoRef.current = dom.parentElement;
    filter(dom.dataset.count);
  };
  useEffect(() => {
    bingoRef.current = bingofirst;
  }, []);
  let bingoRef = useRef();
  let bingofirst;
  return (
    <div className="min-nav" onClick={clickEvent}>
      <ul>
        <li className="bingo" ref={(ref) => (bingofirst = ref)}>
          <h2 data-count="0">当前</h2>
        </li>
        <li>
          <h2 data-count="1">这一周</h2>
        </li>
        <li>
          <h2 data-count="7">这一月</h2>
        </li>
      </ul>

      <style jsx>
        {`
          .min-nav {
            position: fixed;
            top: 25%;
            left: 10%;
            width: auto;
            height: auto;
            transform: translateZ(0);
          }
          @media (max-width: 1500px) {
            .min-nav {
              left: 0%;
            }
          }
          ul {
            list-style: none;
            background: #efaa05;
            color: white;
            border-radius: 6px;
            padding: 1rem;
            z-index: 100;
            width: 6rem;
          }

          li {
            writing-mode: vertical-rl;
            padding: 1rem;
            margin: 1rem 0;
            cursor: pointer;
            border-radius: 4px;
            transition: 0.2s;
          }
          li:hover,
          .bingo {
            background: rgba(255, 255, 255, 0.3);
          }
          h2 {
            font-size: 2rem;
            font-weight: 600;
          }
          @media (max-width: 980px) {
            ul {
              padding: 0rem;
              width: 4rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default MinNav;
