import React,{useState} from 'react'

const File = ({setSrc}) => {
    let fileRef;
    const [filename, setFilename] = useState('')
    const upfileBU = (e) => {       
        e.preventDefault();
        fileRef.click();
    }
    const fileChange = (e) => { 
         let file = e.target.files[0];
         try {
            let blob = URL.createObjectURL(file);
            setFilename(file.name);
            setSrc(blob);
         }catch(err) {
             console.log(err)
         }         
         
    }
    const changeValue = (e) => {
        setFilename(e.target.value);
    }
    const clearValue = (e) => {
            e.preventDefault();
            e.target.previousElementSibling.value = '';
    }
    return (
       <div className='row align-content-between h-100'>
                 <div className="col-12 title d-flex align-items-center p-0">
                    <input type="text" name="photoTitle" placeholder='title' id="photoTitle" className='m-0 p-0 pb-2 text-center'  />                                            
                </div>                      
                <div className="filebu col-12 p-0">
                    <div className="row justify-content-between p-0 m-0 my-5">
                        <input type="text" 
                               name="fileName" value={filename} 
                               onChange={changeValue}
                               onInput={changeValue}
                              id="fileName" className='col px-4 py-2'/>
                        <button className='col-4 offset-1' onClick={upfileBU}>
                             Load Photo
                        </button>
                    </div>
                    <div className="row justify-content-between p-0 m-0 my-5">
                        <input type="text" name="linkUrl" id="linkUrl" className='col px-4 py-2'/>
                        <button className='col-4 offset-1' onClick={clearValue}>
                             Clear Link
                        </button>
                    </div>
                </div>                
                <input type="file" 
                        ref={ref => fileRef = ref }
                        onChange={fileChange}
                       name="photofile" id="photofile" className='d-none'/>
             <style jsx> {`
                     
                   input[name='file-text-title'] {
                       width:60%;                      
                       border:none;
                       border-bottom:solid 2px white;                                                                
                   }
                   .title {
                        height:50%;
                   }
                   .gongneng {
                       height:50%;                       
                   }
                   input {
                       background:none;
                       border:solid 2px white;
                       color:black;
                       font-size:3rem;                                              
                   }
                   .filebu input {
                        border-radius:6px;
                   }
                   button {
                       background:white;
                       border:none;
                       font-size:2rem;
                       font-weight:600;
                       padding:0 1.2rem;
                       border-radius:6px;
                       transition: background .4s;
                   }
                   button:hover {
                        padding:0 1rem;
                        background:none;
                        border:solid 2px white;
                        color:white;
                   }
                        `}
             </style>                                                   
         </div>
    )
}

export default File
