import React, { useState } from "react";

export default () => {
  const [open, setopen] = useState(false);
  return (
    <div className="link-ctn">
      <div className="main pointer ani" onClick={() => setopen((pre) => !pre)}>
        <div className="color"></div>
        <div className="title">
          <h2>CSS</h2>
        </div>
      </div>
      <div className={`site ani pointer ${open ? "" : "close"}`}>
        <div className="backcolor">
          <h3>UI 参考网站 -- dribbbie</h3>
        </div>
      </div>
      <div className={`site ani pointer ${open ? "" : "close"}`}>
        <div className="backcolor">
          <h3>UI 参考网站 -- dribbbie</h3>
        </div>
      </div>
      <div className={`site ani pointer ${open ? "" : "close"}`}>
        <div className="backcolor">
          <h3>UI 参考网站 -- dribbbie</h3>
        </div>
      </div>
      <div className={`site ani pointer test`}>
        <div className="backcolor">
          <h3>UI 参考网站 -- dribbbie</h3>
        </div>
      </div>
      <style jsx>{`
        .link-ctn {
          width: 80%;
          margin: 20px auto;
          position: relative;
        }
        .main {
          padding: 10px 20px;
          background: #212329;
          border-radius: 6px;
          display: flex;
          align-items: center;
          z-index: 10;
        }

        .main > .color {
          width: 66px;
          height: 40px;
          border-radius: 4px;
          margin: 0 20px;
          background: #4eac6d;
        }
        .main > .title {
          flex: 1;
          font-size: 30px;
          text-align: center;
        }

        .site {
          width: 80%;
          margin: 20px 0 20px 20%;
          background: #212329;
          padding: 10px;
          border-radius: 4px;
          position: relative;
          transition: all 0.8s;
        }
        .close {
          position: absolute;
          left: 0;
          top: 0;
          z-index: -2;
          margin: 0 0 0 20%;
        }
        .site .backcolor {
          background: #4eac6d;
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
        .test {
          animation: te 2s 1;
        }
        @keyframes te {
          0% {
            position: absolute;
            left: 0;
          }
          100% {
            position: relative;
            left: 0;
          }
        }
      `}</style>
    </div>
  );
};
