import React,{useEffect}from 'react'
import Link from 'next/link'
import {useRouter} from 'next/router'

const Nav = () => {
    let router = useRouter().asPath;
    
    useEffect(() => {
        let array = ['/','/photo','/videoList','/article-list','/component'];
          array.find((e,index) => {
               if(e === router) {                  
                   let ul = document.querySelector('.min-nav ul');
                   if(ul) {
                     ul.children[index].classList.add('liSeled')
                   }                   
               }
               })
         
    }, [])
    return (
        <div >
            <img src="article-bg.jpg" alt="bg"/>    
            <div className="min-nav row m-0 align-items-center justify-content-center">
                <div className="col-12 col-ls-6">
                    <ul className='d-flex'>
                             <li>
                                <h3>
                                    Home
                                </h3> 
                             </li>
                             <li>
                                 <Link href='/photo'>
                                    <h3>
                                        Photo
                                    </h3>
                                 </Link>
                             </li>
                             <li>
                                <Link href='/videoList'>
                                  <h3>
                                    video
                                   </h3>
                                </Link>
                               
                             </li>
                             <li>
                                <Link href='/article-list'>
                                    <h3>
                                        Article
                                    </h3>
                                </Link>                                
                             </li>
                             <li>
                                <Link href='/component'>
                                    <h3>
                                        Component
                                    </h3>
                                </Link>
                                
                             </li>
                    </ul>
                </div>
            </div>            
                
            <style jsx> {`
               
                 .min-nav {
                     width:100%;
                     min-height:14%;
                     border-bottom:solid rgba(255,255,255,0.5) 1px;
                     background:rgba(0,0,0,0.2);
                     position:absolute;
                     top:0;
                     left:0;
                 }
                 img {
                     width:100%;
                 }
                 ul {
                    width:100%;
                    list-style:none;
                    margin:0;
                 }
                 li {
                     flex:1;
                     text-align:center;
                     color:white;
                     padding:1rem;
                     margin:1rem;
                     border-radius:6px;
                     transition:.4s;
                     cursor:pointer;
                 }

                 li:hover,.liSeled {
                     background:rgba(255,255,255,0.3)
                 }
                 @media (max-width:860px) {
                    h3 {
                        font-size:2rem;
                    }        
                 }
                 @media (max-width:600px) {
                    h3 {
                        font-size:1.5rem;
                    } 
                    .min-nav{
                        min-height:10%;
                    }       
                 }
                        `}
            </style>

        </div>
    )
}

export default Nav
