import React from "react";
import Loading from "../public/anima";
import Photo from "../public/getPhoto";
import Page from "./page";

const ContentList = ({ setState, type, state, loading, count }) => {
  return (
    <div className="row m-0 article-list-root">
      {state.map((item) => (
        <div className="col-10 offset-1 my-5" key={item._id}>
          <Item content={item} />
        </div>
      ))}
      {state.length === 0 && (
        <div className="col-10 offset-1 my-5 not-article-container">
          <div className="row justify-content-center align-items-center">
            {loading ? (
              <Loading />
            ) : (
              <h1 className="col-auto"> 没有相关文章 </h1>
            )}
          </div>
        </div>
      )}
      {count > 10 && <Page type={type} count={count} setState={setState} />}
      <style jsx>
        {`
          .not-article-container .row {
            height: 50vh;
          }
        `}
      </style>
    </div>
  );
};

const Item = ({ content }) => {
  let time = String(new Date(Number(content.createTime)));
  let id = content.ArticleContent.find((item) => item.type === "photo");
  let path = id ? id.filePath : null;

  return (
    <div className="col-12 ">
      <div className="row">
        <div className="col-8">
          <h4 className="type">{content.ArticleType}</h4>
          <h3 className="title my-5">
            <a href={"/article?id=" + content._id} target="_blank">
              {content.ArticleTitle}
            </a>
          </h3>
          <h4 className="create-time">
            {time.slice(0, 10) + "," + time.slice(10, 15)}
          </h4>
        </div>
        <div className="col-4">
          <div className="row align-items-center h-100">
            <Photo id={"addArticle/gotArticlePhoto/" + path} />
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .col-12 {
            border-bottom: 2px solid #eaeaea;
            padding-bottom: 5rem;
          }
          img {
            width: 100%;
          }
          a {
            color: #4f738d;
          }
          a:hover {
            color: #e65e2c;
          }
          h3 {
            font-size: 3rem;
          }
          h4 {
            font-size: 2rem;
            margin: 1rem 0;
            color: #878c8b;
          }
        `}
      </style>
    </div>
  );
};

export default ContentList;
