import React, { useEffect, useState } from "react";
import Link from "next/link";
import { useRouter } from "next/router";

const Nav = (props) => {
  let router = useRouter().asPath;
  const [path] = useState(router);

  return (
    <div className="nav row mx-0">
      <div className="col-12 px-0">{props.children}</div>

      <div className="content">
        <div className="min-nav row align-items-center justify-content-center">
          <div className="col-12 col-sm-10 col-lg-6">
            <ul className="d-flex">
              <li className={path == "/" ? "liSeled" : null}>
                <Link href="/">
                  <h3>Home</h3>
                </Link>
              </li>
              <li className={path == "/photo" ? "liSeled" : null}>
                <Link href="/photo">
                  <h3>Photo</h3>
                </Link>
              </li>

              <li className={path == "/article-list" ? "liSeled" : null}>
                <Link href="/article-list">
                  <h3>Article</h3>
                </Link>
              </li>
              <li className={path == "/component" ? "liSeled" : null}>
                <Link href="/component">
                  <h3>Component</h3>
                </Link>
              </li>
            </ul>
          </div>
        </div>
        {props.title}
      </div>
      <style jsx>
        {`
          .nav {
            position: relative;
            margin-bottom: 10%;
          }
          img {
            width: 100%;
            height: auto;
          }
          .content {
            position: absolute;
            left: 0;
            right: 0;
            top: 0;
            bottom: 0;
          }
          .min-nav {
            width: 100%;
            min-height: 14%;
            border-bottom: solid rgba(255, 255, 255, 0.5) 1px;
          }
          ul {
            width: 100%;
            list-style: none;
            margin: 0;
          }
          li {
            flex: 1;
            text-align: center;
            color: white;
            padding: 1rem;
            margin: 1rem;
            border-radius: 6px;
            transition: 0.4s;
            cursor: pointer;
          }

          li:hover,
          .liSeled {
            background: rgba(255, 255, 255, 0.3);
          }
          @media (max-width: 360px) {
            li {
              margin: 0.4rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Nav;
