import React, { useState, useEffect } from "react";

const Index = () => {
  const [oepn, setoepn] = useState(true);
  let arr = [1, 3, 4, 2];
  const oepnEvent = () => {
    console.log(bigDom.offsetHeight);
    let h = bigDom.offsetHeight;
    bigDom.classList.add("over");
    bigDom.style.height = h;
    // setTimeout(() => {
    //   bigDom.style.height = 0;
    // }, 0);
    // setoepn((pre) => !pre);
  };
  let bigDom;
  return (
    <div className="box-big">
      <button onClick={oepnEvent}>open</button>
      {oepn && (
        <div className="ctn" ref={(ref) => (bigDom = ref)}>
          {arr.map((a, i) => (
            <div className="box" key={i} style={{ top: oepn ? 0 : 0 }}></div>
          ))}
        </div>
      )}
      <style jsx>{`
        .ctn {
          width: 100%;
          height: auto;
          transition: 1s;
        }
        .over {
          overflow: hidden;
        }
        .box {
          width: 80%;
          height: 60px;
          background: blue;
          margin: 10px 0 10px 10%;
          position: relative;
          transition: 0.5s;
        }
      `}</style>
    </div>
  );
};

export default Index;
