import React from 'react'
import { connect } from 'react-redux'

const Sidebar = ({list,action}) => {   
    const filterEvent = (value) => {
         action({
             type:'FILTER_CONTENT',
             filterType:value
         })
    }       
    const search = (e) => {
        let value = e.target.previousElementSibling.value;
        if(value === '' || value === ' '){
            action({
                type:'SHOW_CONTENT',
                title:value
            })    
        }
        action({
            type:'SEACTH_CONTENT',
            title:value
        })
        
    } 
    return (
        <div className='row sidebar shadow justify-content-center'>
            <div className="col-10">
                <input type="text" name="search" 
                   placeholder='Search' id="search"
                   className='w-100 p-2 text-center'/>                
                <i className='iconfont icon-search' onClick={search}></i>
            </div>         
            <div className=" col-10 col-xs-12 mt-4">
                <div className="row">
                {
                    list.map( (e,index) => (
                        <div className="col-auto col-xs-10 my-4" key={index}>
                            <div className="row align-items-center">               
                                <button onClick={() => filterEvent(e)}>
                                    <h3>
                                        {e}
                                    </h3>
                                </button>
                            </div>
                        </div>
                    ))
                }
                </div>
            </div>                                   
            <style jsx> {`
                        .sidebar {
                            padding:8rem 1rem;
                            background:white;
                            border-radius:10px;
                            position:sticky;
                            top:200px;   
                            transform:translateY(-200px);                        
                        }
                        @media (max-width:1200px) {
                            .sidebar {                            
                                    transform:translateY(-100px);   
                                    padding:8rem 1rem;   
                                    top:100px;    
                           }
                         } 
                        i {
                            position:absolute;
                            left:12%;
                            top:50%;
                            transform:translateY(-50%);
                            font-size:3rem;
                            color:#b1b9be;
                            cursor:pointer;
                        }
                        i:hover {
                            color:#fdd303;
                        }
                        .col-10>.row {
                         padding-left:20px;
                        }
                        input[name='search'] {
                            height:6vh;
                            font-size:2rem;
                            font-weight:600;
                            border-radius:2vh;
                            border:solid #e7eaec 2px;
                        }
                                              
                        button {
                            padding:0.5rem 1.5rem;
                            border:#e7eaec solid 2px;
                            border-radius:0.4rem;    
                            margin:0 2rem;   
                            background:#ffffff;                     
                        }
                        button:hover {                            
                            background:#fdd303;
                            border-color:#fdd303;
                            color:white;
                        }
                        `}
       </style>
        </div>
    )
}
const state = (store) =>( {list:store.TypeReducer.list});        
const dispatch = dispatch =>( {action:dispatch});

export default connect(state,dispatch)(Sidebar);
