import React,{useState,useEffect} from 'react'
import {setLocalstorage,setLocalValue} from './localstorage'
import Photo from './authorPhoto'

const Author = () => {
    
    const [TypeValue, setTypeValue] = useState('')
    const inputandchang = (e) => {        
        let value = e.target.value;      
        setTypeValue(value);
        localStorage.setItem('article-type',value);       
    }
    const selectEvent = (e) => {    // 选择文章类型事件
        let dom = e.target;
        if(dom.tagName !== 'H2') return 
        let seled = document.querySelector('.author h2.seled');
        if(seled) seled.classList.remove('seled');
        dom.classList.add('seled');
        setTypeValue(dom.textContent);
        localStorage.setItem('article-type',dom.textContent);    
    }    
    useEffect(() => {
        setLocalValue("input[name='author-name']",'author-name')
        setLocalValue("input[name='article-title']",'article-title')   //获取本地localstorage的信息
        let type = localStorage.getItem('article-type');
        let domList = Array.from(document.querySelectorAll('.author h2'));
        domList.map(e => e.textContent === type ? e.classList.add('seled') :null)        
        if(type) setTypeValue(type)
    },[])
    return (
        <div className='row author  m-0'>
            <div className="col-10 offset-1">
                   <div className="row">
                       <div className="col-12">
                           <h3 className="my-5">
                               AUTHOR
                           </h3>
                           <div className=" d-flex align-items-around">
                                
                                <Photo/>

                                <div className="author-message ml-4">                                   
                                    <input type="text" name="author-name"
                                             id="author-name"
                                             placeholder=' Article Name'  
                                             required   
                                             onChange={(e) => setLocalstorage('author-name',e.target.value)} 
                                             className='h3 p-2 m-0'/>
                                    <input type="text" name="article-title"
                                             id="article-title"
                                             required
                                             onChange={(e) => setLocalstorage('article-title',e.target.value)} 
                                             placeholder=' Article Title'                                             
                                             className='h2 p-2'/>                                   
                                </div>
                           </div>
                       </div>
                       <div className="col-12 mt-5">
                           <ul className='row' onClick={selectEvent}>
                               <li className='col-auto text-center'>
                                    <h2 className='p-3'>
                                        React 
                                    </h2>
                               </li>
                               <li className='col-auto text-center'>
                                    <h2 className='p-3'>
                                        Python
                                    </h2>
                               </li>
                               <li className='col-auto text-center'>
                                    <h2 className='p-3'>
                                        Django
                                    </h2>
                               </li>
                               <li className='col-auto text-center'>
                                    <h2 className='p-3'>
                                        Webpack
                                    </h2>
                               </li>
                               <li className='col'>
                                   <input type="text" name='article-type' 
                                     placeholder='Type'
                                     required
                                     value={TypeValue} 
                                     onInput={inputandchang}
                                     onChange={inputandchang}
                                    id='article-type' className='h-100 px-4'/>
                               </li>
                           </ul>
                       </div>
                       
                   </div>
            </div>
            <style jsx> {`
                       .author {
                           margin-top:10rem;
                           padding-bottom:8rem;
                           border-bottom:2px solid #eaeaea;
                       }      
                       input {
                           height:48%;
                           border:none;
                           border-bottom:solid 1px #c9c9c9;
                       }
                       input[name='article-title'] {
                           width:100%;                          
                       }    
                       li input {
                           font-size:2rem;
                           font-weight:700;
                       }                                  
                       ul {
                           list-style:none;
                       }
                       h2 {
                           border:black solid 2px;
                           cursor:pointer;
                           transition:.2s;
                       }
                       h2.seled {
                          color:white;
                          background:black;
                       }
                       h2:hover {
                           border-color:white;                           
                       }
                       h2.seled:hover {
                            border-color:black;
                            background:white;
                            color:black;
                       }
                        `}
            </style>
        </div>
    )
}

export default Author
