import React, { useEffect, useState } from "react";

const Photo = ({ id, name, classname }) => {
  const [bolb, setBolb] = useState(null);
  const [error, setError] = useState(false);
  const [load, setLoad] = useState(false);
  const getphoto = async () => {
    try {
      const QURL = process.env.DATA_URL + "addphoto/getphoto/" + id;
      setLoad(true);
      let get = await fetch(QURL);
      if (get.ok) {
        let blob = await get.blob();
        let img = document.createElement("img");
        img.src = URL.createObjectURL(blob);
        img.onload = () => {
          document.querySelector(".photo-container").append(img);
          canvasFunc(img.offsetWidth, img.offsetHeight, img, setBolb);
          setLoad(false);
        };
      }
    } catch (err) {
      console.log(err);
      setError(true);
      setLoad(false);
    }
  };
  useEffect(() => {
    getphoto();
  }, [id]);
  return (
    <>
      {bolb && (
        <img src={URL.createObjectURL(bolb)} alt={name} className="w-100" />
      )}
      {load && <div className={"loading " + classname}></div>}
      {error && <h2> 加载图片失败 </h2>}
      <div
        className="photo-container"
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          visibility: "hidden",
          userSelect: "none",
        }}
      ></div>
      <canvas id="canvas" hidden></canvas>

      <style jsx>
        {`
          .loading {
            width: 100%;
            height: 20px;
            background: black;
            height: 20vh;
            animation: show 2s infinite;
            z-index: 99;
          }
          .right {
            height: 44vh;
          }
          @keyframes show {
            0% {
              background: black;
            }
            50% {
              background: #cbcbcb;
            }
            100% {
              background: black;
            }
          }
        `}
      </style>
    </>
  );
};

const canvasFunc = (W, H, img, loadPhoto) => {
  let canvas = document.getElementById("canvas");
  let ctx = canvas.getContext("2d");
  let count = Math.min(W, H);
  canvas.width = count;
  canvas.height = count;
  let w = (Math.max(W, H) - count) / 2;
  W > H ? ctx.drawImage(img, -w, 0) : ctx.drawImage(img, 0, -w); //裁减成一比一
  canvas.toBlob((blob) => {
    // 输出图片
    loadPhoto(blob);
  }, "image/png");
};
export default Photo;
