import React, { useState, useEffect } from "react";

const Index = () => {
  const [oepn, setoepn] = useState(true);
  let arr = [1, 3, 4, 2];
  const closeEvent = () => {
    let h = bigDom.offsetHeight;
    bigDom.classList.add("over");
    bigDom.style.height = h + "px";
    setTimeout(() => {
      let list = document.querySelectorAll(".box");
      Array.from(list).map((d) => (d.style.top = -200));
      bigDom.style.height = 0 + "px";
    }, 0);
  };

  let bigDom;
  return (
    <div className="box-big">
      <button onClick={closeEvent}>open</button>
      {oepn && (
        <div className="ctn" ref={(ref) => (bigDom = ref)}>
          {arr.map((a, i) => (
            <div className="box" key={i}></div>
          ))}
        </div>
      )}
      <style jsx>{`
        .ctn {
          width: 100%;
          transition: 1s;
        }
        .over {
          overflow: hidden;
        }
        .box {
          width: 80%;
          height: 60px;
          background: blue;
          margin: 10px 0 10px 10%;
          position: relative;
          transition: 0.5s;
          top: 0;
        }
      `}</style>
    </div>
  );
};

export default Index;
