import React, { useState } from "react";
import Head from "next/head";
import Nav from "../component/public/nav";
import Root from "../component/component/root";
import Footer from "../component/component/footer";
import Router, { useRouter } from "next/router";
import { Provider } from "react-redux";
import Store from "../component/component/redux/store";
const Component = () => {
  const [routerStatus, setRouterStatus] = useState(false); // 路由变化事件
  const handleRouteChange = () => {
    setRouterStatus(true);
  };
  Router.events.on("routeChangeStart", handleRouteChange);
  console.log(routerStatus);
  let router = useRouter();
  let { type } = router.query;
  type = type ? type : "none";
  return (
    <>
      <Head>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_10szvaedj0wj.css"
        />
        <link rel="icon" href="/logo.jpg" type="image/x-icon"></link>
        <title> 组件库 </title>
      </Head>
      <Provider store={Store}>
        <div className="container-fluid">
          <nav>
            <Nav>
              <img src="com-bg.jpg" alt="bg" />
            </Nav>
          </nav>

          <Root contentType={type} />

          <Footer color="#fdd303" />

          {routerStatus && (
            <div className="loadAnima"> {/*  路由变化时 提示动画 */}</div>
          )}
        </div>
      </Provider>
      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
        img {
          width: 100%;
          height: auto;
        }
        .container-fluid {
          padding: 0;
        }
        @media (max-width: 760px) {
          html {
            font-size: 6px;
          }
        }
      `}</style>
      <style jsx>
        {`
          img {
            width: 100%;
            height: auto;
          }
          .loadAnima {
            position: fixed;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            background: black;
            animation: Loading 2s 10;
            z-index: 9999;
          }
          @keyframes Loading {
            0% {
              opacity: 0;
            }
            50% {
              opacity: 1;
            }
            100% {
              opacity: 0;
            }
          }
        `}
      </style>
    </>
  );
};

export default Component;
