import React, { useState, useEffect, useRef } from "react";
import Content from "./content";
import Sidebar from "./Sidebar";
import { connect } from "react-redux";

const Root = ({ action, contentType }) => {
  const [type, setType] = useState(contentType);
  useEffect(() => {
    setType(contentType);
    getphoto(type);
  }, [contentType]);
  const selectType = (e) => {
    let dom = e.target.closest("h3");
    if (dom) {
      setType(() => dom.textContent);
      getphoto(dom.textContent);
    }
  };
  const getphoto = async (typeV) => {
    try {
      const QURL = process.env.DATA_URL + "getComponentList/all/" + typeV;
      let get = await fetch(QURL);
      if (get.ok) {
        let message = await get.json();
        action({
          type: "GET_TYPE",
          list: message.typeList,
        });
      }
    } catch (err) {
      console.log(err);
    }
  };
  const scrollTop = () => {
    let navDom = document.querySelector(".component-nav");
    navDom.scrollIntoView(true);
  };

  return (
    <div className="row root">
      <div className="col-10 offset-1">
        <div className="row p-0">
          <div className="offset-0 offset-xl-3 col">
            <ul className="d-flex component-nav" onClick={selectType}>
              <li className={type === "React" ? "seled" : ""}>
                <h3>React</h3>
              </li>
              <li className={type === "JS" ? "seled" : ""}>
                <h3>JS</h3>
              </li>
              <li className={type === "CSS" ? "seled" : ""}>
                <h3>CSS</h3>
              </li>
              <li className={type === "Node" ? "seled" : ""}>
                <h3>Node</h3>
              </li>
            </ul>
          </div>
        </div>
        <div className="goTop position-fixed" onClick={scrollTop}>
          <i className="iconfont icon-arrow-forward"></i>
        </div>
        <div className="row">
          <div className="col-12 col-xl-3">
            <Sidebar />
          </div>
          <div className="offset-0 offset-xl-1 col-12 col-xl-8">
            <Content type={type} />
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .root {
            margin: 0;
            margin-bottom: 20rem;
            z-index: 1001;
          }
          .goTop {
            top: 50%;
            right: 10px;
            transform: rotate(-90deg) translateZ(0);
            background: black;
            color: white;
            width: 4rem;
            height: 4rem;
            display: flex;
            justify-content: center;
            align-items: center;
            border-radius: 6px;
            overflow: hidden;
            cursor: pointer;
          }
          .goTop:hover {
            background: white;
            color: black;
          }
          .goTop i {
            font-size: 3rem;
          }
          ul {
            list-style: none;
            background: black;
            color: white;
            margin-bottom: 10rem;
            transform: translateY(-50%);
            position: sticky;
            top: 200px;
            z-index: 1001;
          }
          li {
            flex: 1;
            cursor: pointer;
            transition: 0.4s;
            z-index: 1001;
          }
          li.seled {
            opacity: 0.5;
            z-index: 1001;
          }
          li:hover {
            opacity: 0.5;
            transform: translateY(-25%);
          }
          h3 {
            padding: 2rem;
            font-size: 2.5rem;
            text-align: center;
            z-index: 1001;
          }
        `}
      </style>
    </div>
  );
};
const state = (store) => ({ data: store });
const dispatch = (dispatch) => ({ action: dispatch });

export default connect(state, dispatch)(Root);
