import React, { useEffect, useState } from "react";

const Code = ({ data, clear }) => {
  const [cls, setCls] = useState(false);
  useEffect(() => {
    document.body.style.overflow = "hidden";
    try {
      let { offsetHeight: H } = textRef.parentElement;
      let { offsetHeight: WinH } = textRef.parentElement.parentElement;
      if (H > WinH) {
        setCls(true);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);
  const clearEv = (boob) => {
    document.body.style.overflow = "";
    clear(boob);
  };
  const share = (e) => {
    e.stopPropagation();
    let range = new Range();
    range.setStart(textRef, 0);
    range.setEnd(textRef, 1);
    document.getSelection().removeAllRanges();
    document.getSelection().addRange(range);
    document.execCommand("Copy");
    document.getSelection().removeAllRanges();
    clearEv(true);
  };

  let textRef;
  const stopEv = (e) => {
    e.stopPropagation();
  };
  return (
    <div
      className="row code shadow justify-content-center  align-items-center"
      onClick={() => clearEv(false)}
    >
      <div
        className={`col-10 
        code-content col-md-6 p-5 
        d-flex justify-content-center 
        ${cls ? "start" : ""} `}
        onClick={stopEv}
      >
        <div className="row code-content-ctn">
          <h2 ref={(ref) => (textRef = ref)}>{data}</h2>
        </div>
      </div>
      <div className="col-8 justify-content-center row">
        <button className="col-auto" onClick={share}>
          Cody
        </button>
      </div>
      <style jsx>
        {`
          h2 {
            white-space: pre;
            overflow: auto;
          }
          h2::-webkit-scrollbar {
            display: none;
          }

          .code {
            background: rgba(0, 0, 0, 0.8);
            position: fixed;
            left: 0;
            top: 0;
            bottom: 0;
            right: 0;
            z-index: 9999;
            display: flex;
          }
          .code-content {
            background: white;
            overflow: auto;
            min-height: 50%;
            max-height: 80%;
            border-radius: 20px;
            align-items: center;
          }
          .code-content-ctn {
            max-width: 100%;
            padding: 1rem;
            word-break: break-all;
          }
          .code-content-ctn h2 {
            white-space: normal;
          }
          .start {
            align-items: flex-start;
          }
          button {
            padding: 0.5rem 3rem;
            font-size: 2rem;
            font-weight: 600;
            border: none;
            border-radius: 0.6rem;
            margin: 0 2rem;
            background: #0c82db;
            color: white;
            transition: 0.4s;
          }
          button:hover {
            transform: scale(1.1);
          }
        `}
      </style>
    </div>
  );
};

export default Code;
