import React, { useEffect, useState } from "react";

const Footer = ({ color }) => {
  const [show, setShow] = useState(false);
  useEffect(() => {
    const Event = () => {
      let windo = window.innerHeight;
      let docu = document.documentElement.offsetHeight;
      let top = document.documentElement.scrollTop;
      if (docu - top <= windo) {
        setShow(true);
      } else {
        setShow(false);
      }
    };
    window.addEventListener("scroll", Event);
  }, []);
  return (
    <footer className={"row mx-0 p-0" + show ? "show" : ""}>
      <div className="col-12 py-5 m-0 text-center">
        <h3>In the END</h3>
      </div>
      <style jsx>
        {`
          footer {
            background: ${color};
            color: white;
            right: 0;
            left: 0;
            bottom: 0;
            position: fixed;
            opacity: 0;
            transition: 0.5s;
            transform: translateZ(0);
          }
          .show {
            opacity: 1;
          }
          h3 {
            font-size: 2rem;
          }
        `}
      </style>
    </footer>
  );
};

export default Footer;
