import React, { useState, useEffect, useRef, useCallback } from "react";
import Item from "./item";
const Pagination = React.memo(({ codeList, setCode, type }) => {
  const [count, setCount] = useState(2);
  const [List, setList] = useState([]);
  useEffect(() => {
    //     设置滚动事件
    window.addEventListener("scroll", () => {
      scrollE();
    });
    return () => window.removeEventListener("scroll", scrollE);
  }, [List]);
  useEffect(() => {
    setList(codeList.slice(0, 8)); //     设置初始数
  }, [codeList]);
  const addList = () => {
    setList(codeList.slice(0, count * 8));
    setCount((pre) => (pre += 1));
  };
  let eventId = useRef();

  const scrollE = () => {
    //滚动事件定义
    if (eventId.current) return;
    eventId.current = setTimeout(() => {
      try {
        let scrollCount = Math.round(window.pageYOffset + window.innerHeight);
        if (scrollCount >= document.documentElement.scrollHeight - 2) {
          document.getElementById("loadmore").click();
        }
      } catch (err) {
        console.log(err);
      }
      eventId.current = null;
    }, 500);
  };
  let typevalue = useCallback(
    type === "JS"
      ? "js"
      : type === "React"
      ? "react"
      : type === "CSS"
      ? "css"
      : type === "nodejs"
      ? "node"
      : null,
    [type]
  );
  return (
    <>
      {List.map((e) => (
        <Item setCode={setCode} type={typevalue} key={e._id} message={e} />
      ))}
      <button onClick={addList} id="loadmore" className="d-none">
        click
      </button>
    </>
  );
});

export default Pagination;
