import React from 'react'

const Sidebar = ({selectEv}) => {
    const clickEv = (e) => {
        let value = e.target.closest('li').children[0].textContent
        let id = value  === 'Component' ? 1 : value  === 'Video' ? 2 :value  === 'Photo' ?3 : false;
        selectEv(id);
    }
    return (
        
            <ul className='col-4 sidebar  m-0 p-0' onClick={clickEv}>
               <div className="row justify-content-center px-2">
                   <li className = 'col p-4 '>
                       <h2 className= 'text-center'>
                            Component
                       </h2>
                   </li>
                   <li className = 'col p-4 '>
                       <h2 className= 'text-center'>
                            Video
                       </h2>
                   </li>
                   <li className = 'col p-4 '>
                       <h2 className= 'text-center'>
                            Photo
                       </h2>
                   </li>
               </div>
               <style jsx> {`
                        .sidebar {
                            background:#85c0d2;
                            position:fixed;
                            left:50%;
                            top:0%;
                            transform:translateX(-50%);
                            animation: show 1s 1 ;
                        }
                        @keyframes show {
                            0% {
                                transform:translate(-50%,-120%);
                            }
                            100 {
                                transform:translate(-50%,0%);
                            }
                        }
                        ul {
                            list-style:none;
                            color:white;
                            overflow:hidden;                            
                        }
                        li {
                            cursor:pointer;
                        }
                        li:hover {
                            background:#589baf;
                        }
                        `}
                </style>
            </ul>
           
     
    )
}

export default Sidebar
