import React, { useEffect, useState, useRef } from "react";
import fetch from "node-fetch";
import Head from "next/head";
import Router from "next/router";

import Nav from "../component/articleList/nav";
import MinNav from "../component/articleList/min-nav";
import ContentList from "../component/articleList/contentList";
import Footer from "../component/articleList/footer";

const ArticleList = ({ data, list, count }) => {
  useEffect(() => {
    if (!data) {
      Router.push("/500");
    }
  }, []);
  const [routerStatus, setRouterStatus] = useState(false);
  const handleRouteChange = (url) => {
    setRouterStatus(true);
  };
  Router.events.on("routeChangeStart", handleRouteChange);
  const [state, setState] = useState(data);
  const [initState, setInitState] = useState({ data, count });
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(count);
  const [type, setType] = useState(null);

  return (
    <>
      <Head>
        <title>Article</title>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_piihixc593j.css"
        />
        <link rel="icon" href="/logo.jpg" type="image/x-icon"></link>
      </Head>
      <div className="container-fluid">
        <nav className="row mx-0">
          <Nav />
        </nav>

        <MinNav
          setState={setState}
          setType={setType}
          setPage={setPage}
          setLoad={setLoading}
          list={list}
          oldState={initState}
          setPage={setPage}
        />

        <ContentList
          state={state}
          type={type}
          setState={setState}
          loading={loading}
          count={page}
        />

        <Footer />
        {routerStatus && (
          <div className="loadAnima"> {/*  路由变化时 提示动画 */}</div>
        )}
      </div>

      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
        .container-fluid {
          padding: 0;
          overflow-x: hidden;
        }
        @media (max-width: 600px) {
          html {
            font-size: 6px;
          }
        }
        @media (max-width: 400px) {
          html {
            font-size: 5px;
          }
        }
      `}</style>
    </>
  );
};

export const getServerSideProps = async () => {
  const RURL = process.env.DATA_URL + "addArticle/gotArticleList/1";
  let res = await fetch(RURL);
  if (res.status === 404) return { props: { data: null } };
  let result = await res.json();
  return {
    props: {
      data: result.data,
      list: result.list,
      count: result.count,
    },
  };
};
export default ArticleList;
