import React from 'react'
import Photo from './photo'
const Item = ({title,link,photoName,createTime,photoId}) => {    
    let time = new Date(Number(createTime));
    let yeah = time.getFullYear();
    let month = time.getMonth();
    let day = time.getDate();     
    const clickLink = (e) => {
        e.target.previousElementSibling.click();
    }  
    return (  
        <div className='row mx-0 item justify-content-center'>
              <div className="col-10 col-lg-8">
                  <div className="row ">
                      <div className="col-12 col-xl-6">
                          <Photo id={photoId} name={photoName}/>                          
                          <div className="row text-center pl-4 create-time my-3 my-xl-5">
                                <h3>
                                     {day +' - ' +(month+1) +' - ' +yeah } 
                                </h3>
                           </div> 
                      </div>
                      <div className="col-12 col-xl-6">
                          <div className="row h-100 align-items-center ">
                              <div className="col-12 d-flex flex-column  align-items-center">
                                <h2 className="photo-item-title text-left text-xl-center  my-3 my-xl-5">
                                    {
                                        title                                                
                                    }                                   
                                </h2>
                                <a href={link} className='d-none'target='_blank'> link</a>
                                <button className='w-50 my-5'onClick={clickLink} >
                                    go to link
                                </button> 
                            </div>
                          </div>
                      </div>
                  </div>
              </div> 
             
              <style jsx> {`
                     .item{
                         margin-top:10rem;
                     }   
                    
                     h2 {
                         font-size:3rem;
                     }
                     h3 {
                         font-size:2.5rem;
                         color:#efaa05;
                     }
                     button {
                         background:#efaa05;
                         border:none;
                         font-size:2rem;
                         font-weight:600;
                         border-radius:6px;
                         color:white;
                         padding:1rem 3rem;
                         cursor:pointer;
                         transition:.3s;
                     }
                     button:hover {
                         box-shadow:0 0 6px rgba(0,0,0,0.2);
                         font-size:3rem;
                     }
                        `}
              </style>            
        </div>
       
   
    )
}

export default Item
