import React, { useEffect, useState } from "react";

import Head from "next/head";
import Nav from "../component/release-article/nav";
import Author from "../component/release-article/author";
import ArtContent from "../component/release-article/articleContent";
import About from "../component/release-article/about";
import Footer from "../component/release-article/footer";

const Home = () => {
  const uploadData = async (data) => {
    try {
      const QURL = process.env.DATA_URL + "addArticle/add";
      let res = await fetch(QURL, {
        method: "POST",
        body: data,
      });
      let result = await res.json();
      if (res.ok) {
        let keys = Object.keys(localStorage);
        for (let key of keys) {
          if (key !== "author-name" && key !== "article-key") {
            localStorage.removeItem(key);
          }
        }
        let openRequest = indexedDB.open("article-store", 2);
        openRequest.onsuccess = async () => {
          let db = openRequest.result;
          let tx = db.transaction("photoBuffer", "readwrite");
          let store = tx.objectStore("photoBuffer");
          let keys = store.getAllKeys();
          keys.onsuccess = () => {
            keys.result.map((key) => {
              if (key !== "article-author-photo") {
                store.delete(key);
                console.log("delete");
              }
            });
            window.location.reload();
          };
        };
      } else {
        alert(result);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const submitForm = (e) => {
    e.preventDefault();
    let data = new FormData(e.target);
    let content = localStorage.getItem("article-content-object");

    let fileList = JSON.parse(content).filter((item) => item.type === "photo");
    fileList.push({
      key: "article-author-photo",
    });
    let openRequest = indexedDB.open("article-store", 2);
    openRequest.onsuccess = async () => {
      let db = openRequest.result;
      let tx = db.transaction("photoBuffer");
      let store = tx.objectStore("photoBuffer");
      let reqs = fileList.map(
        (item) =>
          new Promise((resolve, reject) => {
            let photo = store.get(item.key);
            photo.onsuccess = () => {
              if (photo.result) {
                resolve({
                  file: photo.result.file,
                  name: item.key,
                });
              } else {
                reject("有一个图片板块没有文件。请上传或是删除点这个板块");
              }
            };
          })
      );
      Promise.all(reqs)
        .then((items) => {
          items.map((item) => {
            data.append(item.name, item.file);
          });
          data.append("article-content-main", content);
          data.append("link-count", LinkList.length);
          uploadData(data);
        })
        .catch(alert);
    };
  };

  const [LinkList, setLinkList] = useState(["a"]);

  return (
    <>
      <Head>
        <title>编写文章</title>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_7p01375xskx.css"
        />
        <link rel="icon" href="/logo.jpg" type="image/x-icon"></link>
      </Head>
      <div className="container-fluid">
        <nav>
          <Nav>
            <img src="article-bg.jpg" alt="bg" />
          </Nav>
        </nav>
        <form onSubmit={submitForm}>
          <Author />

          <ArtContent setLinkList={setLinkList} />

          <About List={LinkList} />
        </form>

        <Footer />
      </div>

      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
        .container-fluid {
          padding: 0;
        }
      `}</style>
    </>
  );
};

export default Home;
