import React,{ useEffect , useState } from 'react'
import PostBu from './PostBu'
import Link from 'next/link'
import {useRouter} from 'next/router'
import Router from 'next/router'

const Nav = () => {
    let router = useRouter().asPath;  
    
     const [routerStatus, setRouterStatus] = useState(false)
      const handleRouteChange = url => {       
        setRouterStatus(true)
      }     
      Router.events.on('routeChangeStart', handleRouteChange)     
    

    useEffect(() => {
        let array = ['/','/photo','/videoList','/article-list','/component'];
          array.find((e,index) => {
               if(e === router) {                  
                   let ul = document.querySelector('.min-nav ul');
                   if(ul) {
                     ul.children[index].classList.add('liSeled')
                   }                   
               }
               })
         
    }, [])

    
    return (
        <div className='col-12 mx-0 px-0'>
            
            <img src="article-list-bg.jpg" alt="bg"/>    
            <div className="main-nav row m-0 align-items-center justify-content-center">
                <div className="col-12 col-md-10  col-ls-6">
                    <ul className='d-flex'>
                              <li>
                                <Link href='/'>
                                    <h3>
                                        Home
                                    </h3> 
                                </Link>
                             </li>
                             <li>
                                 <Link href='/photo'>
                                    <h3>
                                        Photo
                                    </h3>
                                 </Link>
                             </li>
                             <li>
                                <Link href='/videoList'>
                                  <h3>
                                    video
                                   </h3>
                                </Link>
                               
                             </li>
                             <li>
                                <Link href='/article-list'>
                                    <h3>
                                        Article
                                    </h3>
                                </Link>                                
                             </li>
                             <li>
                                <Link href='/component'>
                                    <h3>
                                        Component
                                    </h3>
                                </Link>
                                
                             </li>                             
                    </ul>
                </div>                
            </div>   
            <PostBu/>            
           { routerStatus &&  <div className='loadAnima'>           {/*  路由变化时 提示动画 */}
                                </div> }      
            <style jsx> {`
                                 
                 .main-nav {
                     width:100%;
                     min-height:14rem;
                     border-bottom:solid rgba(255,255,255,0.5) 1px;
                     background:rgba(0,0,0,0.2);
                     position:absolute;
                     top:0;
                     left:0;
                 }
                 img {
                     width:100%;
                 }
                 ul {
                    width:100%;
                    list-style:none;
                    margin:0;
                 }
                 li {
                     flex:1;
                     text-align:center;
                     color:white;
                     padding:1rem;
                     margin:1rem;
                     border-radius:6px;
                     transition:.4s;
                     cursor:pointer;
                 }

                 li:hover,.liSeled {
                     background:rgba(255,255,255,0.3);
                 }

                 @media (max-width:1000px) {
                    div.main-nav {
                        min-height:6rem;
                    }
                 }
                 @media (max-width:600px) {
                       
                 }
                 .loadAnima {
                     position:fixed;
                     left:0;
                     top:0;
                     right:0;
                     bottom:0;
                     background:black;
                     animation:Loading 2s 10;
                     z-index:9999;
                }                
                @keyframes  Loading {
                     0% {
                          opacity: 0;
                     }
                     50% {
                         opacity:1;
                     }
                     100% {
                         opacity:0;
                     }
                 }  

                        `}
            </style>

        </div>
    )
}

export default Nav
