import React , { useRef , useEffect ,useState } from 'react'

const Nav = ({ list , setList }) => {
    
    let dom = useRef();  
    const [Type, setType] = useState(list[0].typeName)

    useEffect(() => {           //初步获取首个活动的元素
        setTimeout(() => {
             let selectDom = document.querySelector('.nav .nav-item');                                        
             selectDom.classList.add('seled')
             dom.current = selectDom                                 
        },200)
     },[])
    const selectContent = (data,e) => {      
        
        try {
            let target = e.target.closest('li');    //选中的元素添加样式
            if(!target) return;
            dom.current.classList.remove('seled');
            target.classList.add('seled');
            dom.current = target;
            setType(() => target.children[0].textContent);  
            setList(data.typeList)
        }catch(err) {
            if(err) console.log(err);
        }
  }    
 
    return (
        <div className='nav row'>            
            <div className="col-8 m-auto">
                <ul className='row justify-content-center'>
                    <li className="col-12 title text-center">
                       <h1 className='component-code-typeName'>
                           { Type }
                       </h1>
                    </li> 
                        {
                            list.map(
                                item => <li className="col-10 nav-item text-center" key={item._id} onClick={ e => selectContent(item,e)}>
                                            <h3>
                                                {item.typeName}
                                            </h3>
                                        </li> 
                            )
                         }                                        
                </ul>            
            </div>
            
            <style jsx> {`
                  .nav {                                          
                      background:white;
                  }
                  ul {
                    list-style:none;
                  }
                  li {
                      padding:2rem;
                      margin:0;
                      transition:.4s;
                      cursor:pointer;
                      margin:4px 0;
                  }
                  li.seled,li:hover{
                      background:#4b30c3;
                      color:white;
                  }
                        `}
             </style>
        </div>
    )
}

export default Nav
