import React , {useEffect , useRef} from 'react'

const Page = ({ count ,setState, type}) => {    
    let item = 1;
    let array = [item];
    while (count > 10) { 
        count -= 10;
        array.push(++item)
    }    

    let domRef = useRef() 

    useEffect(() => {
         let dom = document.querySelector('.page')
         dom.classList.add('current')
         domRef.current = dom;
    },[])

    const gotData = async (page) => {  
        try {
             const RURL = process.env.DATA_URL+`${type?'addArticle/gotArticle/type/'+type+'?page=':'addArticle/gotArticleList/'}`+page             
             let res = await fetch(RURL)
             let result = await res.json();           
             return result
        }catch(err){
            console.log(err);
            return {
                err,
            }
        } 
   }

   const ClickEvent = async (e) => {       
       let dom = e.target.closest('.page')
       if(!dom || dom.classList.contains('current')) return        
       let page = +dom.children[0].textContent;
       let data =await gotData(page);       
       if(Array.isArray(data.data)){
            setState(data.data);
            domRef.current.classList.remove('current'); 
            dom.classList.add('current')
            domRef.current = dom;
            let navDom = document.querySelector('.min-nav');
            navDom.scrollIntoView(true);
        }
       
   }

    return (
        <div className='col-6 mx-auto mt-5'>
            <div className="row justify-content-center" onClick={ClickEvent}>                
                {
                    array.map((count,index) => (
                        <div className="col-auto page  mx-2" key={index} >
                            <h2 > {count} </h2>
                        </div>
                        
                    ))
                }
            </div>            
            <style jsx>{`
                        .page {
                            border:solid #4f738d  2px;
                            color:#4f738d;
                            width:60px;
                            height:60px;
                            display:flex;
                            justify-content:center;
                            align-items:center;
                            cursor:pointer;
                        }
                        .page:hover,.page.current {
                            background:#4f738d;
                            color:white;
                        }
                        h2 {
                            font-weight:700;
                        }
                        @media (max-width:600px) {
                            .page {
                                width:40px;
                                height:40px;
                            }
                       }

                                                        `}</style>
        </div>
    )
}

export default Page
