import React, { useState, useEffect } from "react";
import Photo from "../public/getPhoto";
const ArtContent = ({ content }) => {
  const [load, setload] = useState(false);
  useEffect(() => setload(true), []);

  const shareEvent = () => {
    let textDom = document.querySelector(".share-dom");
    if (textDom) {
      let range = new Range();
      range.setStart(textDom, 0);
      range.setEnd(textDom, 1);
      document.getSelection().removeAllRanges();
      document.getSelection().addRange(range);
      document.execCommand("Copy");
      document.getSelection().removeAllRanges();

      alert("复制成功");
    }
  };
  return (
    <div className="row Content mx-0 justify-content-center">
      <div className="col-10 col-ls-6 ">
        {content.map((item) => {
          if (item.type === "text") {
            return (
              <div className="row content-item" key={item.key}>
                <h3>{item.value}</h3>
              </div>
            );
          } else {
            return (
              <React.Fragment key={item.key}>
                <div className="row content-item-title justify-content-center">
                  <h3 className="title ">{item.title}</h3>
                </div>
                <div className="row photo">
                  <Photo id={"addArticle/gotArticlePhoto/" + item.filePath} />
                </div>
              </React.Fragment>
            );
          }
        })}
        <div className="row align-items-center">
          <h2>分享</h2>
          <h3 className="share-dom position-fixed" style={{ left: "-10000px" }}>
            {load && window.origin + window.history.state.url}
          </h3>
          <div>
            <i className="iconfont mx-4 icon-attach" onClick={shareEvent}></i>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .Content {
            margin-top: 10rem;
            border-bottom: 2px solid #eaeaea;
          }
          .content-item {
            margin-bottom: 5rem;
          }
          .content-item-title {
            margin-bottom: 3rem;
          }
          h2 {
            font-size: 3rem;
            line-height: 2;
          }
          h3 {
            font-size: 2.5rem;
            line-height: 2;
          }
          i {
            color: white;
            font-size: 2rem;
            padding: 0.5rem;
            background: #3a5999;
            border-radius: 50%;
            cursor: pointer;
          }
          .title {
            font-size: 4rem;
            font-weight: 700;
          }
          .photo {
            margin-bottom: 3rem;
            display: flex;
          }
        `}
      </style>
    </div>
  );
};

export default ArtContent;
