import React, { useEffect, useRef } from "react";

function Link({ mess, open, gao, setgao, mes, cor }) {
  let once = useRef(false);
  useEffect(() => {
    let { current } = once;
    current ? (open ? zeroEv() : positionEv()) : (once.current = true);
  }, [open]);

  useEffect(() => {
    if (mess) positionEv();
  }, [mess]);
  const positionEv = () => {
    let posi = dom.getBoundingClientRect();
    if (!gao) setgao(dom.offsetHeight);
    dom.style.top = -(posi.top - mess.top) + "px";
  };
  const zeroEv = () => {
    dom.style.top = 0;
  };
  let dom;

  return (
    <div className="site ani pointer test " ref={(ref) => (dom = ref)}>
      <div className="min-ctn ">
        <div className="backcolor" style={{ backgroundColor: cor }}>
          <a href={mes.url} target="_black">
            <h3>{mes.name}</h3>
          </a>
        </div>
      </div>
      <style jsx>{`
        .site {
          width: 80%;
          margin-left: 20%;
          background: #212329;
          padding: 10px;
          border-radius: 4px;
          position: relative;
        }
        .min-ctn {
          margin: 20px 0;
        }
        a {
          text-decoration: none;
        }
        .site .backcolor {
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
      `}</style>
    </div>
  );
}

export default Link;
