import React, { useEffect, useState, useRef } from "react";

const Skill = () => {
  const scrollEv = () => {
    if (skillRef) {
      if (!DomList.current) return;
      let height = window.innerHeight / 2;
      let top = skillRef.getBoundingClientRect().top;
      if (top < height) {
        DomList.current.map((e) => e.classList.remove("jin"));
      } else {
        DomList.current.map((e) => e.classList.add("jin"));
      }
    }
  };
  let DomList = useRef();
  useEffect(() => {
    window.addEventListener("scroll", scrollEv);
    DomList.current = Array.from(skillRef.querySelectorAll(".img"));
  }, []);
  let skillRef;
  return (
    <div
      className="skill row justify-content-center mx-0"
      ref={(ref) => (skillRef = ref)}
    >
      <div className="col-6 ">
        <h2 className="text-center m-5">技能</h2>
      </div>
      <div className="col-10 my-5">
        <div className="row justify-content-center mb-5">
          <div className="skill-photos d-flex">
            <div className="img jin">
              <img src="/kuangjia/react.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/js.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/vue.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/html.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/css.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/boot.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/next.png" alt="" />
            </div>
            <div className="img jin">
              <img src="/kuangjia/mongo.png" alt="" />
            </div>
          </div>
        </div>
      </div>
      <style jsx>
        {`
          .skill {
            padding: 14rem 0;
          }
          h2 {
            font-size: 4rem;
          }
          .img {
            height: 100px;
            width: 50px;
            margin-left: 20px;
            transition: 0.5s;
          }

          .skill-photos {
            padding-right: 60px;
          }
          @media (max-width: 960px) {
            .img {
              height: 75px;
              width: 37.5px;
            }
            .skill-photos {
              padding-right: 60px;
            }
          }
          @media (max-width: 520px) {
            .img {
              height: 40px;
              width: 20px;
            }
            .skill-photos {
              padding-right: 40px;
            }
          }
          @media (max-width: 360px) {
            .img {
              height: 34px;
              width: 17px;
            }
            .skill-photos {
              padding-right: 34px;
            }
          }
          .jin {
            width: 2px;
          }
          .img:hover {
            z-index: 2;
            transform: scale(1.2);
          }
          img {
            height: 100%;
            filter: drop-shadow(0 0 2px #c1c1c2);
          }
        `}
      </style>
    </div>
  );
};

export default Skill;
