import React from "react";

function Link() {
  const positionEv = (e) => {
    let posi = e.currentTarget.getBoundingClientRect();
    let MainPosi = mainDom.getBoundingClientRect();
    e.currentTarget.style.top = -(posi.top - MainPosi.top) + "px";
  };
  return (
    <div className={`site ani pointer test`} onClick={positionEv}>
      <div className="backcolor">
        <h3>UI 参考网站 -- dribbbie</h3>
      </div>
      <style jsx>{`
        .site {
          width: 80%;
          margin: 20px 0 20px 20%;
          background: #212329;
          padding: 10px;
          border-radius: 4px;
          position: relative;
          top: 0;
          z-index: -1;
        }
        .close {
          position: absolute;
          left: 0;
          top: 0;

          margin: 0 0 0 20%;
        }
        .site .backcolor {
          background: #4eac6d;
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
      `}</style>
    </div>
  );
}

export default Link;
