import React, { useState, useEffect } from "react";
import Load from "../public/loading";

const Index = ({ openEvent }) => {
  useEffect(() => {
    //自动输入账号
    let account = localStorage.getItem("user-account");
    if (account) {
      let input = document.querySelector("input[name='account']");
      input.value = account;
      document.querySelector("input[name='password']").focus();
    }
  }, []);
  const submitEv = async (e) => {
    e.preventDefault();
    let data = new FormData(e.target);
    setLoading(true);
    try {
      const QURL = process.env.DATA_URL + "login";
      let res = await fetch(QURL, {
        method: "POST",
        body: data,
      });
      let result = await res.json();
      if (res.ok) {
        localStorage.setItem("user-account", data.get("account"));
        document.documentElement.style.background = "#f7f7f7";
        openEvent(result);
      } else {
        setLoading(false);
        alert(result);
      }
    } catch (err) {
      console.log(err);
    }
  };
  const [Loading, setLoading] = useState(false);
  return (
    <div className="row m-0">
      <div className="main-root shadow-sm">
        <div className="row">
          <div className="animation col-12 d-flex align-items-center justify-content-center ">
            {Loading ? (
              <Load color="#97cad9" />
            ) : (
              <i className="iconfont icon-suo"></i>
            )}
          </div>
          <form className="form-root col-12" onSubmit={submitEv}>
            <h1 className="title my-5">Login</h1>
            <label htmlFor="account" className="col-12 h3 my-4">
              Account
            </label>
            <input
              type="text"
              name="account"
              id="account"
              autoFocus
              required
              className="w-100"
            />
            <label htmlFor="password" className="col-12 h3 my-4">
              Password
            </label>
            <input
              type="password"
              name="password"
              id="password"
              required
              className="w-100"
            />
            <button type="submit" className="my-5 h2 shadow-sm">
              LOGIN
            </button>
          </form>
        </div>
      </div>
      <style jsx>
        {`
          .main-root {
            width: 480px;
            position: fixed;
            top: 50%;
            left: 50%;
            border-radius: 20px;
            overflow: hidden;
            transform: translate(-50%, -50%);
            background: white;
          }
          .animation {
            height: 200px;
            background: #fafeff;
          }
          .form-root {
            padding: 10%;
            padding-top: 0;
          }
          i {
            font-size: 60px;
            color: #97cad9;
          }
          input {
            font-size: 3rem;
            border-radius: 20px;
            border: solid #e5e5e5 2px;
            padding: 1rem;
          }
          label {
            margin: 0;
            padding: 0;
            color: #d5d6d9;
          }
          button {
            width: 100%;
            padding: 1rem;
            font-size: 3rem;
            background: #f3fcfe;
            border: none;
            color: #97cad9;
            font-weight: 700;
            border-radius: 10px;
            transition: 0.2s;
          }
          button:hover {
            transform: scale(1.1);
          }
          h1 {
            color: #97cad9;
          }
        `}
      </style>
    </div>
  );
};

export default Index;
