import React, { useEffect, useState, useRef } from "react";
import Head from "next/head";
import Index from "../component/login/index";
import AddCom from "../component/login/addComponenttwo/root";
import AddPhoto from "../component/login/addPhoto/upPhoto";
import AddVideo from "../component/login/addVideo/UpVideo";
import Sidebar from "../component/login/sidebar";

const Home = () => {
  const [Key, setKey] = useState("");
  const [CentId, setCentId] = useState(0);
  const openEvent = (key) => {
    setKey(key);
    setCentId(1);
  };

  const seleceWindow = (id) => {
    if (id === false) return;
    let BGColor = id === 3 ? "#85c0d2" : id === 2 ? "#334a52" : "#f7f7f7";
    document.documentElement.style.background = BGColor;
    if (id === CentId) return;
    setCentId(id);
  };
  const resizeLoad = (index) => {
    setCentId(() => 5);
    setCentId(() => index);
  };
  return (
    <>
      <Head>
        <title>Create Next App</title>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_7p01375xskx.css"
        />
      </Head>
      <div className="container-fluid">
        {CentId === 0 && <Index openEvent={openEvent} />}
        {CentId === 1 && <AddCom rest={resizeLoad} />}
        {CentId === 2 && <AddVideo />}
        {CentId === 3 && <AddPhoto rest={resizeLoad} />}
        {CentId > 0 && <Sidebar selectEv={seleceWindow} />}
      </div>

      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
          background: #f7f7f7;
          background: #383636;

          transition: 0.5s;
        }
        .container-fluid {
          padding: 0;
        }
      `}</style>
    </>
  );
};

export default Home;
