import React,{useEffect,useState} from 'react'
import File from './file'
import Photo from './photo'

const UpPhoto = ({rest}) => {
  
  const submitEvent = async (e) => {
         e.preventDefault();
         console.log(e.target.tagName);      
         let data = new FormData(e.target);
         try {
           let res = await fetch('http://localhost:4040/addPhoto/photo',{
              method:'POST',
              body:data,
          })         
          let result = await  res.json();    
          if(res.ok) {                
             rest(3);
          }else{                        
                console.log(result);
          }
      }catch(err) {
          console.log(err);
      }  
         
  } 
  const [SRC, setSRC] = useState('');
 
  return(  
  <>          
         <form onSubmit={submitEvent}>
            <div className=" row justify-content-center photo-main-root"> 
                   
                    <div className="col-9">
                        <div className="row">                          
                                <Photo file={SRC} clear={() => setSRC(null)}/>
                                <div className="col offset-1">
                                        <File setSrc = { setSRC }/>            
                                </div>
                          
                        </div>                                         
                    </div>                                                                                       
            </div>
       </form>
      
      <style jsx> {`                
       .photo-main-root {
                margin:0;
                width:100%;
                background:#85c0d2;
                position:fixed;
                top:50%;
                transform:translateY(-50%);
                animation: show 1s 1 ;
        }  
       @keyframes show {
                            0% {
                                transform:scale(1.2);
                            }
                            100 {
                              transform:scale(1);
                            }
                        }
         
                        `}
       </style>
  
  </>
)}

export default UpPhoto
