import React from 'react'

const ShowVideo = () => {
    return (
       <div className="col-4 show">
                     <Form/>
                     <div className="row h-100 flex-column justify-content-center align-items-center">
                          <div className="show-logo d-flex justify-content-center align-items-center">
                              <i className='iconfont icon-cloud-upload'></i>
                          </div>
                          <h1 className='my-5'>
                              Upload a New Video
                          </h1>
                          <button>
                               Upload a File
                          </button>
                     </div>  
                     <style jsx> {`
                          .show {
                             background:white;
                             height:660px;
                             border-radius:20px;
                             position:relative; 
                             overflow:hidden;
                          }
                          .show-logo {
                              height:120px;
                              width:120px;
                              background:#e9e9eb;
                              border-radius:20px;
                          }
                          i {
                              color:#9192a4;
                              font-size:90px;
                          }
                          button {
                                padding:1rem 1.5rem;
                                font-size:2rem;
                                background:black;                                                                                    
                                border-radius:10px;
                                transition:.2s;
                                font-weight:700;
                                color:white;
                                border:2px solid black;
                                border-color:rgba(0,0,0,0);
                            }
                         button:hover {
                                color:black;
                                border:2px solid black;
                                background:none;  
                        }
                        `}
                   </style>                                       
       </div> 
    )
}

export default ShowVideo


const Form = () => {
    return(    
            <div className="col-12 form-root shadow">
                <div className="row h-100 justify-content-center align-content-center">
                    <label htmlFor="title" className='h1 my-4 col-10'>
                        Video Title
                    </label>                   
                    <input type="text" name="title" id="title" className='col-10'/>
                    <label htmlFor="title" className='h2 my-5 col-10'>
                        Video Introduction
                    </label> 
                    <textarea name="Introduction" id="Introduction" className=' col-10'></textarea>
                    <button className='mt-5'>
                        Upload a File
                    </button>
                </div>
                <style jsx> {`
                        .form-root {
                              position:absolute;
                              right:0;
                              top:0;
                              bottom:0;
                              left:0;
                              background:white;
                              z-index:3;
                              border-radius:20px;
                              transition:.4s;
                              transform:translateX(110%);
                        }
                        input {
                            font-size:3rem;
                            border-radius:12px;
                            border:solid black 2px;
                            padding:1rem;
                            
                        } 
                        textarea {
                            height:200px;
                            border:solid 2px black;
                            border-radius:12px;
                            font-size:2rem;
                            padding:2rem;
                           
                        } 
                        button {
                                padding:1rem 1.5rem;
                                font-size:2rem;
                                background:black;                                                                                    
                                border-radius:10px;
                                transition:.2s;
                                font-weight:700;
                                color:white;
                                border:2px solid black;
                                border-color:rgba(0,0,0,0);
                            }
                         button:hover {
                                color:black;
                                border:2px solid black;
                                background:none;  
                        }
                        `}
                </style>
            </div>
    )
}
